 class acl { public $acl=array("all"=>512); public $acldb=false; public $aclid=array("all"=>0); public $pwd=''; public $date=array('begin'=>0,'end'=>0); public $hours=array('begin'=>0,'end'=>0); public $weeks=array('begin'=>0,'end'=>0); public $roledisable=array(); public $error_method=''; public function aclCheck($router,$action='') { if(!isset($this->acl[$action])){ if($this->acl['all']!=0) { $this->acl[$action]=$this->acl['all']; $action='all'; }else{ Return true; } } if($this->acl[$action]==0){ Return true; } if(isset($this->aclid[$action])&&in_array($this->aclid[$action],MY()->acl)) { $this->error=L("  "); if(empty($this->error_method)){ $this->error_method='noPassport'; } Return false; } $mask=0; if($this->acl[$action]&1) { if(MY()->isLogin()) { $mask=1; }else { $this->error.=L("  "); if(empty($this->error_method)){ $this->error_method='noLogin'; } } } if($this->acl[$action]&2) { if(MY()->isLogin()) { $mask=$mask+2; }else { $this->error.=L("  "); if(empty($this->error_method)){ $this->error_method='noLogin'; } } } if($this->acl[$action]&4) { if(count(array_diff(explode(",",$this->aclgroup[$action]),MY()->group))==0) { $mask=$mask+4; }else { $this->error.=L("  "); if(empty($this->error_method)){ $this->error_method='noGroup'; } } } if($this->acl[$action]&8) { if(count(array_diff(explode(",",$this->aclrole['all'].",".$this->aclrole[$action]),MY()->group))==0) { $mask=$mask+8; }else { $this->error.=L("  "); if(empty($this->error_method)){ $this->error_method='noRole'; } } } if($this->acl[$action]&16) { if(count(array_intersect($this->roledisable,array_merge(MY()->array_multi2single(MY()->grouprole),MY()->role)))==0) { $mask=$mask+16; }else { $this->error.=L("  "); if(empty($this->error_method)){ $this->error_method='noAccess'; } } } if($this->acl[$action]&32) { if($this->checkVisitDate()) { $mask=$mask+32; }else { $this->error.=L("  "); if(empty($this->error_method)){ $this->error_method='noDate'; } } } if($this->acl[$action]&64) { if($this->checkVisitWeeks()) { 